pragma solidity ^0.5.0;

contract Marketplace {
    string public name;

    uint public productCount =0;

    mapping(uint => Product)  public products;

    struct Product {
        uint id;
        string name;
        string nameimg;
        uint price;
        address owner;
        bool purchased;
        
    }

    event ProductCreated(
        uint id,
        string name,
        string nameimg,
        uint price,
        address owner,
        bool purchased
    );
 
    constructor() public {
        name = "UTAS Market";
    }
    //, string memory _imgOfCar 
    function createProduct(string memory _name  ,string memory _nameimg  , uint _price) public {
        //Make sure parameters are correct
        //incorement product
        productCount ++;

        //Create the product
        products[productCount] = Product(productCount, _name, _nameimg , _price, msg.sender, false );
        
        //Trigger on event
        emit ProductCreated(productCount, _name, _nameimg , _price, msg.sender, false);

    }

}
